mapper.update:
update userfans
set fan_follow = 1
where user_id = #{user_ID}
  and fan_id = #{fan_id}

update userfans
set fan_follow = 0
where user_id = #{user_ID}
  and fan_id = #{fan_id}

update user
set user_password=#{user_Password}
where user_email = #{user_Email}

mapper.delete:
delete
from userfollow
where user_id = #{user_ID}
  and follow_id = #{ID}

delete
from userfans
where user_id = #{ID}
  and fan_id = #{user_ID}

mapper.select:
select user_password
from user
where user_email = #{user_Email}

select user_ID
from user
where user_ID = #{id}

select user_name
from user
where user_email = #{user_Email}

select user_Email
from user
where user_email = #{user_Email}

select user_ID, user_name, user_sex, user_age, user_avatar, user_birthday, user_introduction
from user
where user_ID = #{user_ID}

select user_name
from user
where user_name = #{user_Name}

select user_ID, user_name, user_avatar, user_email
from user
where user_email = #{user_Email}

select user_ID
from user
where user_email = #{user_Email}

select user.user_ID, user_name, user_avatar, user_introduction
from userfollow,
     user
where userfollow.user_id = #{user_ID}
  and userfollow.follow_id = user.user_ID

select *
from userfans
where userfans.user_id = #{user_ID}
  and fan_id = #{ID}

select *
from userfollow
where user_id = #{user_ID}
  and follow_id = #{ID}
